mapper.select:
SELECT board.*, co_name AS bo_co_name, SUM(CASE WHEN re_state = 1 THEN 1 ELSE 0 END) AS bo_up, SUM(CASE WHEN re_state = - 1 THEN 1 ELSE 0 END) AS bo_down FROM board JOIN community ON co_num = bo_co_num LEFT JOIN recomment ON re_bo_num = bo_num where
bo_title like concat('%', #{cri.search}, '%') or bo_me_id = #{cri.search}
bo_title like concat('%', #{cri.search}, '%')
bo_me_id = #{cri.search}
GROUP BY bo_num ORDER BY bo_num DESC LIMIT #{cri.pageStart}, #{cri.perPageNum}

  		select count(*) from board 
  		where


				bo_title like concat('%', #{cri.search}, '%')or
				bo_me_id = #{cri.search}


				bo_title like concat('%', #{cri.search}, '%')


				bo_me_id = #{cri.search}
